    /**
     * Function:
     *         RVA    -> 0x00000000
     *         Name   -> 
     *         Flags  -> ()
     * Parameters:
     *         Fn                                                 
     *         const void*                                        instance
     *         const size_t&                                      index
     */
    template<typename Fn>
    Fn GetVFunction(const void* instance, const size_t& index)
    {
        auto vtable = *static_cast<const void***>(const_cast<void*>(instance));
        return reinterpret_cast<Fn>(const_cast<void (*)>(vtable[index]));
    }

