#include <cstdint>
#include <vector>
#include <string>

#ifdef _MSC_VER
    #pragma pack(push, 0x01)
#endif

namespace CG::BasicTypes
{
    /**
     * 
     * Size -> 0x0000
     */
    template<typename T, typename TBase>
    class TAutoPointer : public TBase
    {
    public:
        operator T*() const
        {
            return TBase::Get();
        }
        operator const T*() const
        {
            return (const T*)TBase::Get();
        }
        operator bool() const
        {
            return TBase::Get() != nullptr;
        }
    };
}

#ifdef _MSC_VER
    #pragma pack(pop)
#endif
